css:
form {
  display: grid;
  grid-template-columns: auto 20px auto auto;
  grid-row-gap: 7px;
}

fieldset {
  display: content;
  grid-column: 2 / 5;
  border: 0;
  padding: 0;
  margin: 0;
}

fieldset > legend {
  display: none;
}

label {
  grid-column: 1;
  text-align: right;
  margin-right: 10px;
}

input[type=text], input[type=email], input[type=url], input[type=password] {
  grid-column: 2 / 5;
}

input[type=checkbox] + label {
  grid-column: 3 / 5;
  text-align: left;
}

form > div {
  grid-column: 2 / 5;
}
